<script>
import Lingallery from 'lingallery';
export default {
  components:{
    Lingallery
  },
  data(){
    return {
      galleries: [],
      currentId:null,
      coords: [42.340782,69.596329],
      placemarks: [],
    }
  },
  async asyncData({params, $axios, store}) {
    let galleries = [];
    let form = {}
    let saveColor = '';
    const alias = params.alias
    const place = await $axios.$get('/single-place/'+alias);
    form.place_id = place.id
    if (store.$auth.$state.loggedIn){
      form.user_id = store.$auth.$state.user.user.id
      if (place.savings.length>0){
        place.savings.forEach((item,i) => {
          if (item.user_id == store.$auth.$state.user.user.id){
            saveColor = 'color--red'
          } else {
            saveColor = ''
          }
        })
      }
    }
    let TIMA = JSON.parse(place.address_link)
    let placemarks = []
    TIMA.forEach((item, i) => {
      placemarks[i] = [item.lat, item.lng];
    })

    if(place.galleries.length > 0){
      for(let i = 0; i < place.galleries.length; i++){
        galleries.push(
          {id:place.galleries[i].id,src:store.state.image.image +place.galleries[i].image , thumbnail:store.state.image.image + place.galleries[i].image}
        )
      }
    }
    galleries.push({
      id:100, src:store.state.image.image +place.image, thumbnail:store.state.image.image +place.image
    })
    return {place,galleries,placemarks, form, saveColor}
  },
  methods:{
    getImages(data){
      // console.log(this.$store.state.image.image);
      return this.$store.state.image.image + data ;
    },
    truncate(string, value) {
      return string.substring(0, value) + '…';
    },
    async addSave(){
      // console.log(this.form)
      try {
        this.$toast.show('Updating in...')
        await this.$axios.$post("/cabinet/add-save", this.form).then((response) => {
          this.$toast.success('Успешно добавлен')
          this.saveColor = response
          // window.location.reload()
        }).catch(({response}) => {
          if (response.status === 401){
            window.location.assign('/login')
          }
          // this.errors = response.data.errors
        })
      } catch (e) {
        this.$toast.error('Error')
        console.log(e)
      }
    }

  },
  mounted() {
    console.log(this.place)
  }
}
</script>

